namespace Solution
{
    using NUnit.Framework;
    using System;
    using System.Collections.Generic;

    [TestFixture]
    public class Sample_Test
    {
        private static IEnumerable<TestCaseData> testCases
        {
            get
            {
                yield return new TestCaseData(1).Returns("0");
                yield return new TestCaseData(2).Returns("01");
                yield return new TestCaseData(5).Returns("01101");
                yield return new TestCaseData(10).Returns("0110100110");
            }
        }

        [Test, TestCaseSource("testCases")]
        public string Test(int n) => Kata.ThueMorse(n);
    }

    [TestFixture]
    public class Random_Test
    {
        private static string thueMorseString = "";

        private static Random rnd = new Random();

        private static string solution(int n) => thueMorseString.Substring(0, n);

        [Test]
        public void RandomTest()
        {
            const int Tests = 10000;
            const int Min = 1;
            const int Max = 10000;

            for (int i = 0; i < Tests; ++i)
            {
                int n = rnd.Next(Min, Max + 1);

                string expected = solution(n);
                string actual = Kata.ThueMorse(n);

                Assert.AreEqual(expected, actual);
            }
        }
    }
}